const d=()=>{let i=document.querySelectorAll(".gallery2"),a=document.querySelector(".filtering"),o=document.querySelectorAll(".filtering"),c=new Isotope(".gallery2",{itemSelector:".items",masonry:{columnWidth:".width2"}});if(i.length>=1&&i.forEach(l=>{c=new Isotope(l,{itemSelector:".items",masonry:{columnWidth:".width2"}})}),a){a.addEventListener("click",function(t){if(matchesSelector(t.target,"span")){var e=t.target.getAttribute("data-filter");e=e,c.arrange({filter:e})}});const l=t=>{t.addEventListener("click",e=>{matchesSelector(e.target,"span")&&(t.querySelector(".active").classList.remove("active"),e.target.classList.add("active"))})};for(var r=0,n=o.length;r<n;r++){var s=o[r];l(s)}}};export{d as i};
